import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function RegistroUsuario() {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [rut, setRut] = useState('');
  const [edad, setEdad] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nombre || !apellido || !rut || !edad) {
      setError('Por favor, completa todos los campos');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/usuarios', {
        nombre,
        apellido,
        rut,
        edad,
      });

      alert('Usuario registrado con éxito');
      navigate('/');
    } catch (err) {
      if (err.response?.status === 409) {
        setError('Ya existe un usuario con ese RUT');
      } else {
        setError('Error al registrar usuario');
      }
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Registro de Usuario</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nombre" value={nombre} onChange={(e) => setNombre(e.target.value)} /><br /><br />
        <input type="text" placeholder="Apellido" value={apellido} onChange={(e) => setApellido(e.target.value)} /><br /><br />
        <input type="text" placeholder="RUT" value={rut} onChange={(e) => setRut(e.target.value)} /><br /><br />
        <input type="number" placeholder="Edad" value={edad} onChange={(e) => setEdad(e.target.value)} /><br /><br />
        <button type="submit">Registrarse</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
}

export default RegistroUsuario;
