import { useState } from 'react';
import axios from 'axios';

function RegistroUsuario() {
  const [form, setForm] = useState({
    nombre: '',
    apellido: '',
    rut: '',
    edad: '',
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/usuarios/registro', form);
      alert('Usuario registrado con éxito');
    } catch (err) {
      setError(err.response?.data?.message || 'Error al registrar');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registro de Usuario</h2>
      <input name="nombre" placeholder="Nombre" onChange={handleChange} required />
      <input name="apellido" placeholder="Apellido" onChange={handleChange} required />
      <input name="rut" placeholder="RUT" onChange={handleChange} required />
      <input name="edad" type="number" placeholder="Edad" onChange={handleChange} required />
      <button type="submit">Registrar</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </form>
  );
}
